import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    // Automatisches Anzeigen des Pop-ups nach dem Laden der Komponente
    setShowPopup(true);
  }, []);

  const closePopup = () => {
    setShowPopup(false);
  };

  return (
    <div>
      {showPopup && (
        <div className="popup">
          <div className="popup-inner">
            <button onClick={closePopup}>Schlie√üen</button>
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/Robzc9p1l50"
              frameborder="0"
              allow="autoplay; encrypted-media"
              allowfullscreen
              title="Video"
            ></iframe>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
