import React, { useEffect } from 'react';

function MyComponent() {
    useEffect(() => {
        function updateClock() {
            const now = new Date();
            const hours = now.getHours();
            const minutes = now.getMinutes().toString().padStart(2, '0');
            document.getElementById('clock').innerHTML = hours + ':' + minutes;
        }

        const intervalId = setInterval(updateClock, 1000);

        // Embed specific YouTube video
        const videoID = 'Robzc9p1l50';
        document.getElementById('youtube-section').innerHTML = '<iframe width="100%" height="100%" src="https://www.youtube.com/embed/' + videoID + '" frameborder="0" allowfullscreen></iframe>';

        // Bereinige den Interval, wenn die Komponente unmountet wird
        return () => clearInterval(intervalId);
    }, []);

    return (
        <div>
            <div id="youtube-section">
                {/* YouTube iframe will be embedded here */}
            </div>
            <div id="info-section">
                <div id="calendar">Kalender</div>
                <div id="clock">Uhrzeit</div>
            </div>
        </div>
    );
}

export default MyComponent;
