import React, { useState } from 'react';
import './App.css';

function App() {
  const [showPopup, setShowPopup] = useState(false);

  const togglePopup = () => {
    setShowPopup(!showPopup);
  };

  return (
    <div>
      <button onClick={togglePopup}>Video anzeigen</button>

      {showPopup && (
        <div className="popup">
          <div className="popup-inner">
            <button onClick={togglePopup}>Schlie√üen</button>
            <iframe
              width="560"
              height="315"
              src="https://www.youtube.com/embed/Robzc9p1l50"
              frameborder="0"
              allow="autoplay; encrypted-media"
              allowfullscreen
              title="Video"
            ></iframe>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
